mapper.select:
SELECT a.* FROM appeal AS a, user AS u
WHERE
    a.user_id = u.user_id
AND a.class_id = #{classId}

    AND a.user_id IN (SELECT user_id FROM user WHERE username = CONCAT("%",#{username},"%"))


    AND a.user_id IN (SELECT user_id FROM user WHERE user_number = CONCAT("%",#{userNumber},"%"))


    AND state = #{state}

LIMIT #{number1}, #{number2}

SELECT COUNT(a.user_id) FROM appeal AS a, user AS u WHERE a.user_id = u.user_id AND a.class_id = #{classId}
AND a.user_id IN (SELECT user_id FROM user WHERE username = CONCAT("%",#{username},"%"))
AND a.user_id IN (SELECT user_id FROM user WHERE user_number = CONCAT("%",#{userNumber},"%"))
AND state = #{state}

SELECT a.* FROM appeal AS a, user AS u WHERE a.user_id = u.user_id AND a.user_id IN (SELECT user_id FROM user WHERE user_number = CONCAT("%",#{userNumber},"%"))
AND state = #{state}
LIMIT #{number1}, #{number2}
