mapper.select:
SELECT a.* FROM appeal AS a, user AS u
WHERE
    a.user_id = u.user_id
AND a.class_id = #{classId}
LIMIT #{number1}, #{number2}

SELECT a.* FROM appeal AS a, user AS u WHERE a.user_id = u.user_id AND a.user_id IN (SELECT user_id FROM user WHERE user_number = #{userNumber})
AND state = #{state}
LIMIT #{number1}, #{number2}
