mapper.select:
SELECT * FROM user WHERE identity = 1 AND user_id != all(
    SELECT user_id FROM class
    )

SELECT u.user_id, u.username, u.user_number FROM user AS u, student AS s WHERE
u.user_id = s.user_id
AND s.class_id = #{classId}
LIMIT #{number1}, #{number2}

SELECT COUNT(s.user_id) FROM user AS u, student AS s WHERE
u.user_id = s.user_id
AND s.class_id = #{classId}

SELECT u.user_number, u.username, u.identity, a.score FROM user AS u, student AS s, appraisal AS a
WHERE u.user_id = s.user_id AND u.user_id = a.user_id

    AND u.user_number LIKE CONCAT("%",#{userNumber},"%")


    AND u.username LIKE CONCAT("%",#{username},"%")


    AND s.class_id = #{classId}


    AND a.month = #{month}


    AND u.identity = #{role}


    ORDER BY score


    ORDER BY score desc

LIMIT #{number1}, #{number2}
